import React from 'react';
import {Formik, Form, Field, ErrorMessage} from "formik";
import * as Yup from 'yup';
import styles from './RegistrationForm.module.scss';

const RegistrationForm = () => {

    const validationSchema = Yup.object({
        email: Yup.string().email('Не правильный email').required('Укажите email!'),
        name: Yup.string()
            .min(2, 'Минимальное количество символов 2')
            .max(15, 'Максимальное количество символов 15')
            .required('Это обязательное поле!'),
        surname: Yup.string()
            .min(2, 'Минимальное количество символов 2')
            .max(15, 'Максимальное количество символов 15')
            .required('Это обязательное поле!'),
        password: Yup.string()
            .min(6, 'Минимальное кол-во символов 6')
            .required('Это обязательное поле!'),
        confirmPassword: Yup.string()
            .required('Повторите пароль')
            .oneOf([Yup.ref('password'), null], 'Пароли не совпадают')

    })


    return (
        <>
            <Formik
                initialValues={{
                    email: '',
                    name: '',
                    surname: '',
                    password: '',
                    confirmPassword: ''
                }}
                validationSchema={validationSchema}
                onSubmit={(values, {setSubmitting}) => {
                    console.log(values);
                    setSubmitting(false)
                }}>
                {(formik) => {
                    return (
                        <div className={styles.formWrapper}>
                            <Form className={styles.form}>
                                <div className={styles.inputWrapper}>
                                    <span>Email: <ErrorMessage name={'email'}/></span>
                                    <Field
                                        className={styles.input}
                                        type='email'
                                        name='email'
                                        placeholder={'email'}/>
                                </div>
                                <div className={styles.inputWrapper}>
                                    <span>Имя <ErrorMessage name={'name'}/></span>
                                    <Field
                                        className={styles.input}
                                        type='text'
                                        name='name'
                                        placeholder={'Имя'}/>
                                </div>
                                <div className={styles.inputWrapper}>
                                    <span>Фамилия<ErrorMessage name={'name'}/></span>
                                    <Field
                                        className={styles.input}
                                        type='text'
                                        name='surname'
                                        placeholder={'Фамилия'}/>
                                </div>
                                <div className={styles.inputWrapper}>
                                    <span>Password</span>
                                    <Field
                                        className={styles.input}
                                        type='password'
                                        name='password'
                                        placeholder={'Пароль'}/>
                                </div>
                                <div className={styles.inputWrapper}>
                                    <span>Confirm Password</span>
                                    <Field
                                        className={styles.input}
                                        type='password'
                                        name='confirmPassword'
                                        placeholder={'Подтвердите пароль'}/>
                                </div>
                            </Form>
                        </div>
                    )
                }}
            </Formik>
        </>
    );
};

export default RegistrationForm;
