import {getRopesOrder} from "./selectors";
import {addRopeToOrder} from "./actions";

export const addRopeOrder = (color_id, quantity, ropesOrder) => dispatch => {
    if (ropesOrder) {
        const order = {}
        dispatch(addRopeToOrder(color_id, quantity))
    }
}

export const ropesOrderOperation = (quantityAction, color_id, quantity) => dispatch => {
    switch (quantityAction) {
        case 'increment':
            dispatch(addRopeToOrder(color_id, quantity))
            console.log('+');
            return;
        case 'decrement':
            console.log('-');
            return;
        case 'input':
            console.log('+-');
            return;
        default:
            return;
    }
}
