import {loginUser} from "./actions";

export const loginUserOperation = (email, name, surname, shopAddress, token) => dispatch => {
    const user = {email, name, surname, shopAddress, token, isAuth: true}
    dispatch(loginUser(user));
}

export const checkAuthOperation = () => dispatch => {
    const token = localStorage.getItem('token');

}
