import React, {useState} from 'react';
import {NavLink} from "react-router-dom";
import styles from './MenuBarLink.module.scss';
import Icons from "../Icons/Icons";

const MenuBarLink = ({icon = 'userIcon', path, name, color}) => {

    const [isHovered, setIsHovered] = useState(false);
    const [isActive, setIsActive] = useState(false);
    const [linkStyle, setLinkStyle] = useState(`${styles.link}`)
    const [activePath, setActivePath] = useState('');

    const handleMouseEnter = () => {
        setIsHovered(!isHovered);
    }

    const handleClick = () => {
        setIsActive(!isActive);
        setActivePath(path);
        if (activePath !== path) {
            setLinkStyle(`${styles.activeLink}`)
            setActivePath(path);
        } else {
            setLinkStyle(`${styles.link}`)
        }
    }

    return (
        <NavLink
            className={linkStyle}
            to={path}
            onMouseEnter={() => handleMouseEnter()}
            onMouseLeave={() => handleMouseEnter()}
            onClick={() => handleClick()}
        >
            {/*<ul className={styles.ul}>*/}
                <li className={styles.li}>
                    <span className={styles.iconWrapper}>
                        <Icons
                            type={icon}
                            width={'35px'}
                            height={'35px'}
                            color={isHovered || isActive ? '#6495EDFF' : color}
                        />
                    </span>
                    <span className={styles.text}>{name}</span>
                </li>
            {/*</ul>*/}
        </NavLink>

    );
};

export default MenuBarLink;
