import {getRopesOrder} from "./selectors";
import {addRopeToOrder} from "./actions";

export const addRopeOrder = (color_id, quantity, ropesOrder) => dispatch => {
    if (ropesOrder) {
        const order = {}
        dispatch(addRopeToOrder(color_id, quantity))
    }
}

export const ropesOrderOperation = (order, quantityAction, color_id, quantity) => dispatch => {

    switch (quantityAction) {
        case 'increment':
            console.log(order);
            order.forEach(item => {
                if (item.color_id === color_id) {
                    console.log('remove');

                    // dispatch(addRopeToOrder({color_id, quantity}))
                }
            })
            dispatch(addRopeToOrder({color_id, quantity}))
            console.log('+');
            return;
        case 'decrement':
            console.log('-');
            return;
        case 'input':
            console.log('+-');
            return;
        default:
            return;
    }
}
