import {useCallback, useEffect, useState} from "react";
import {useDispatch} from "react-redux";
// import {loginUser} from "../store/User/actions";
import {loginUserOperation} from "../store/User/operations";
import {getNewAccessTokenAPI} from "../http/userAPI";

export const useAuth = () => {
    const [email, setEmail] = useState();
    const [name, setName] = useState();
    const [surname, setSurname] = useState();
    const [shop_id, setShopId] = useState();
    const [token, setToken] = useState();
    const [isActivated, setIsActivated] = useState(false)
    const dispatch = useDispatch();

    const login = useCallback( (email, name, surname, shop_id, jwtToken, isActivated) => {
        setEmail(email);
        setName(name);
        setSurname(surname);
        setShopId(shop_id);
        setToken(jwtToken);
        setIsActivated(isActivated)

        // const newAccessToken = await getNewAccessTokenAPI();
        // const user = {email, name, surname, shop_id, jwtToken: newAccessToken.data.accessToken}
        // console.log(newAccessToken);
        dispatch(loginUserOperation(email, name, surname, shop_id, jwtToken, isActivated))
        localStorage.setItem('jwtToken', JSON.stringify(jwtToken));
    }, [])

    useEffect(async () => {
        const token = JSON.parse(localStorage.getItem('jwtToken'));
        console.log(token);
        if (token) {
            const newAccessToken = await getNewAccessTokenAPI();
            console.log(newAccessToken);
            const newToken = newAccessToken.data.accessToken;
            const {email, name, surname, shop_id, isActivated} = newAccessToken.data.user;
            // const {email, name, surname, shop_id, jwtToken} = userData
            login(email, name, surname, shop_id, newToken, isActivated);
        }
    }, [login])

    return {login}
}
