import React from 'react';
import {Form, Formik, Field, ErrorMessage} from "formik";
import * as Yup from 'yup';
import styles from './AuthenticateForm.module.scss';
import {useHttp} from "../../../Hooks/http.hook";

const AuthenticateForm = () => {
    const { request } = useHttp();

    const validationSchema = Yup.object({
        email: Yup.string().email('Не правильный email').required('Укажите email!'),
        password: Yup.string().required('Это обязательное поле!')
    })

    return (
        <>
            <Formik
                initialValues={{
                    email: '',
                    password: ''
                }}
                validationSchema={validationSchema}
                onSubmit={async (values, {setSubmitting}) => {
                    const { email, password } = values;
                    const authUser = await request('/user/login', 'POST', {email, password})
                    console.log(values);
                    setSubmitting(false)
                }}
            >
                {(formik) => {
                    return (
                        <div className={styles.formWrapper}>
                            <Form className={styles.form}>
                                <label>
                                    <div>
                                        User name:
                                        <span className={styles.errorMessage}>
                                            <ErrorMessage name={'email'}/>
                                        </span>
                                    </div>
                                    <Field className={styles.input} type={'email'} name={'email'}/>
                                </label>

                                <label>
                                    <div>
                                        Password:
                                        <span className={styles.errorMessage}>
                                            <ErrorMessage name={'password'}/>
                                        </span>
                                    </div>
                                    <Field className={styles.input} type={'password'} name={'password'}/>
                                </label>

                                <div>
                                    <button className={styles.submitButton} type={'submit'}>Зарегистрироваться</button>
                                </div>
                            </Form>
                        </div>
                    )
                }}
            </Formik>
        </>
    );
};

export default AuthenticateForm;
