import React, {useEffect, useState} from 'react';
import styles from './OrdersPage.module.scss';
import Button from "../../Components/Button/Button";
import {getAllRopesBrand} from "../../http/ropesAPI";

const OrdersPage = () => {
    const [shopAddresses, setShopAddresses] = useState();
    const [brands, setBrands] = useState(null);


    useEffect(async () => {
        // здесь нужно получать данные из базы, адресса магазинов и названия брендов и их id
        const brands = await getAllRopesBrand();
        setBrands(brands.data)
        console.log(brands.data);
    }, [])


    const ordersByShop = () => {
        console.log('check shop orders')
    }

    const orderByBrand = brands !== null && brands.map(brand => {
        const {id, brandName} = brand;
        return (
            <option value={brandName}>{brandName}</option>
        )
    })


    // для селектов нужно будет получать инфу из бд
    // сделать селекты для выбора магазина и бренада
    return (
        <div className={styles.pageContainer}>
            <header className={styles.header}>
                <h1 className={styles.title}>Заказы со склада</h1>
                <div className={styles.headerButtons}>
                    <div>
                        <span>Выберете магазин: </span>
                        <select name="" id="">
                            {orderByBrand}
                            {/*<option value="">rhrhrh</option>*/}
                            {/*<option value="">rhrhrh</option>*/}
                            {/*<option value="">rhrhrh</option>*/}
                            {/*<option value="">rhrhrh</option>*/}
                        </select>
                    </div>
                    <div>
                        <span>select brand</span>
                        <select name="" id="">
                            <option value="">djjdjd</option>
                            <option value="">djjdjd</option>
                            <option value="">djjdjd</option>
                            <option value="">djjdjd</option>
                            <option value="">djjdjd</option>
                        </select>
                    </div>
                    {/*<Button text={'Заказы по магазинам'} onClick={() => ordersByShop()} />*/}
                    {/*<Button text={'Заказы по брендам'} onClick={() => orderByBrand()} />*/}
                </div>

            </header>
            <main className={styles.body}>

            </main>
        </div>
    );
};

export default OrdersPage;
