import React, {useEffect, useState} from 'react';
import Container from "../../Components/Container/Container";
import styles from './UserPage.module.scss';
import {useSelector} from "react-redux";
import {getIsActivated, getUserId} from "../../store/User/selectors";
import {getOrderByUser} from "../../http/orderAPI";
import Order from "../../Components/Order/Order";

const UserPage = () => {
    const isActivated = useSelector(getIsActivated);
    const userId = useSelector(getUserId);
    const [orderData, setOrderData] = useState(null);
    const [orderStatus, setOrderStatus] = useState(null);
    const [orders, setOrders] = useState(null);


    useEffect(async () => {
        const orderData = await getOrderByUser(userId);
        console.log(orderData);
        setOrderData(orderData)
        // const {order, orderDetails} = orders.data;
        // const {order_status} = order;
        // setOrders(orderDetails)
        // setOrderStatus(order_status);
        // setOrderData(orders.data);
    }, [])


    const showOrders = orderData !== null && orderData.map(order => {
        const {color_id, quantity} = order;
        return (
            <div>
                <p>{color_id}</p>
                <p>{quantity}</p>
            </div>
        )
    })

    return (
        <Container>
            <h1>
                user page
                {/*{showOrders}*/}
                {
                    !isActivated &&
                        <div>
                            please check your gmail to activate account
                        </div>
                }
            </h1>
            <Order />
        </Container>
    );
};

export default UserPage;
