import {useCallback, useEffect, useState} from "react";
import {useDispatch} from "react-redux";
// import {loginUser} from "../store/User/actions";
import {loginUserOperation} from "../store/User/operations";

export const useAuth = () => {
    const [email, setEmail] = useState();
    const [name, setName] = useState();
    const [surname, setSurname] = useState();
    const [shopAddress, setShopAddress] = useState();
    const [token, setToken] = useState();
    const dispatch = useDispatch();

    const login = useCallback((email, name, surname, shopAddress, jwtToken) => {
        setEmail(email);
        setName(name);
        setSurname(surname);
        setShopAddress(shopAddress);
        setToken(jwtToken);

        const user = {email, name, surname, shopAddress, jwtToken}
        dispatch(loginUserOperation(email, name, surname, shopAddress, jwtToken))
        localStorage.setItem('userData', JSON.stringify(user));
    }, [])

    useEffect(() => {
        const userData = JSON.parse(localStorage.getItem('userData'));
        console.log(userData);
        if (userData && userData.token) {
            login({...userData});
        }
    }, [login])

    return {login}
}
