import {useCallback, useEffect, useState} from "react";
import {useDispatch} from "react-redux";
// import {loginUser} from "../store/User/actions";
import {loginUserOperation} from "../store/User/operations";
import {getNewAccessTokenAPI} from "../http/userAPI";

export const useAuth = () => {
    const [email, setEmail] = useState();
    const [name, setName] = useState();
    const [surname, setSurname] = useState();
    const [shop_id, setShopId] = useState();
    const [token, setToken] = useState();
    const dispatch = useDispatch();

    const login = useCallback(async (email, name, surname, shop_id, jwtToken) => {
        setEmail(email);
        setName(name);
        setSurname(surname);
        setShopId(shop_id);
        setToken(jwtToken);

        const newAccessToken = await getNewAccessTokenAPI();
        const user = {email, name, surname, shop_id, jwtToken: newAccessToken.data.accessToken}
        console.log(newAccessToken);
        dispatch(loginUserOperation(email, name, surname, shop_id, newAccessToken.data.accessToken))
        localStorage.setItem('userData', JSON.stringify(user));
    }, [])

    useEffect(() => {
        const userData = JSON.parse(localStorage.getItem('userData'));
        console.log(userData);
        if (userData && userData.jwtToken) {
            const {email, name, surname, shop_id, jwtToken} = userData
            login(email, name, surname, shop_id, jwtToken);
        }
    }, [login])

    return {login}
}
