import React, {useEffect, useState} from "react";
import AppRoutes from "./routes/AppRoutes";
import {getIsActivated, getIsAuth} from "./store/User/selectors";
import {useDispatch, useSelector} from "react-redux";
import {useAuth} from "./Hooks/auth.hook";
import {authFunctionsActions} from "./store/User/actions";
import AuthPage from "./Pages/AuthPage/AuthPage";


function App() {
    const dispatch = useDispatch();
    const isAuth = useSelector(getIsAuth);
    const isActivated = useSelector(getIsActivated);
    const {login} = useAuth();

    useEffect(() => {
        dispatch(authFunctionsActions(login));
    }, [])

  return (
      <>
          {isAuth && isActivated ? <AppRoutes/> : <AuthPage/>}

          {/*<Header/>*/}
          {/*<AddProductsPage/>*/}
        {/*<OrderPage/>*/}
      </>
  );
}

export default App;
